    font-size: 16px;
    display: block;
    cursor: pointer;
    padding: 10px 0;
    margin: 10px 0;
    color: #174567;
    font-weight: 600;
    background-image: url(show.png);
    background-repeat: no-repeat;
    background-position: 100% 50%;
    
}

input:checked+.terms-showMore+* {
    max-height: 2000px;
    overflow: hidden;
    transition: max-height 1s,overflow 1s;
}

input+.terms-showMore+* {
    font-size: 15px;
    padding-left: 15px;
    text-align: left;
    color: #666;
    max-height: 0;
    overflow: hidden;
    transition: max-height 1s,overflow 1s;
    border-bottom: 1px solid darkgray !important;
}

